mapper.select:

SELECT y.* FROM(
			SELECT rownum as rm, x.*FROM(
						SELECT * FROM user_data
						)x)y
WHERE y.rm BETWEEN #{startRow} AND #{endRow}


SELECT y.* FROM(
			SELECT rownum as rm, x.*FROM(
						SELECT * FROM admin_data
						)x)y
WHERE y.rm BETWEEN #{startRow} AND #{endRow}

SELECT y.* FROM(
SELECT rownum as rm, x.* FROM (
SELECT * FROM user_data


		WHERE user_name like '%'||#{query}||'%'


		WHERE user_id LIKE '%'||#{query}||'%'


) x
) y
WHERE y.rm BETWEEN #{page.startRow} AND
#{page.endRow}

SELECT y.* FROM(
SELECT rownum as rm, x.* FROM (
SELECT * FROM
admin_data


		WHERE admin_name like '%'||#{query}||'%'


		WHERE admin_id LIKE '%'||#{query}||'%'


) x
) y
WHERE y.rm BETWEEN #{page.startRow} AND
#{page.endRow}
